import React from 'react';
import { Link } from 'react-router-dom';

import Routes from '../../config/name.routes';
import Slidebar from './Slidebar';

interface propsToolbar {
  heightVh: number;
}

const Toolbar = ({ heightVh }: propsToolbar) => {
  return (
    <header
      className="w-full h-14 black-500 flex items-center lg:justify-center"
      style={{ height: `${heightVh}vh`, minHeight: 56 }}
    >
      <div className="lg:hidden">
        <Slidebar />
      </div>
      <div className="absolute flex items-center justify-center w-full lg:w-11/12">
        <h1 className="text-xl font-bold text-white lg:text-center lg:whitespace-nowrap">
          <Link title="Home" to="/">Calculadora de Teoria de Colas</Link>
        </h1>
        <div className="hidden lg:flex lg:w-full lg:justify-end lg:items-center">
          <NavItem name="About" route={Routes.about} />
          <NavItem name="Ejemplos" route={Routes.theory} />
          <NavItem name="Formulas" route={Routes.about} />
        </div>
      </div>
    </header>
  );
};

interface NavItemProps {
  name: string;
  route: string;
}

const NavItem = ({ name, route }: NavItemProps) => {
  return (
    <Link
      title={name}
      className={`px-4 font-medium text-white py-8 rounded-sm
      hover:bg-opacity-20 hover:bg-white
      `}
      to={route}
    >
      <p>{name}</p>
    </Link>
  );
};

export default Toolbar;
